(function(){function show(options){var renderIcon=options.items.filter(function(o){return o.ironIcon=='check';}).length;if(renderIcon){for(var i=0,length=options.items.length;i<length;i++){var option=options.items[i];switch(option.ironIcon){case'check':option.name='• '+option.name;break;default:option.name='  '+option.name;break;}}}
var innerOptions={'title':options.title,'buttonLabels':options.items.map(function(i){return i.name;})};if(options.showCancel!==false){innerOptions.addCancelButtonWithLabel=Globalize.translate('ButtonCancel');}
window.plugins.actionsheet.show(innerOptions,function(index){if(options.callback){if(index>=1&&options.items.length>=index){options.callback(options.items[index-1].id);}}});}
window.ActionSheetElement={show:show};})();